var _;

_ = require('underscore');


/**
 * Some helpers to deal with the elastic.io platform
 */

module.exports = {
  getCommonConfig: function(cfg) {
    var config;
    return config = {
      client_id: cfg.sphereClientId,
      client_secret: cfg.sphereClientSecret,
      project_key: cfg.sphereProjectKey,
      timeout: cfg.timeout || 60000
    };
  },
  returnSuccess: function(message, next) {
    return next(null, message);
  },
  returnFailure: function(error, message, next) {
    if ((next == null) && _.isFunction(message)) {
      next = message;
      message = error.message;
    }
    return next(error, message);
  }
};
